import React, { useRef, useState } from "react";

import calsses from "./MealItemForm.module.css";
import Input from "../../UI/Input";

const MealItemForm = () => {
  const amountInputRef = useRef();
  return (
    <form className={calsses.form}>
      <Input
        label="Amount"
        input={{
          id: "amount",
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
        }}
      />
      <button>+ Add</button>
    </form>
  );
};
export default MealItemForm;
